import React, { useState, useEffect } from 'react';
import { eventsAPI } from '../services/api';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await eventsAPI.getAll();
        setEvents(response.data);
      } catch (err) {
        setError('Gagal memuat data event');
        console.error('Error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, []);

  if (loading) return <div className="loading">Memuat data event...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="section">
      <h1 className="section-title">Event Pariwisata</h1>
      <p style={{ textAlign: 'center', marginBottom: '2rem', fontSize: '1.2rem' }}>
        Jadwal event dan festival budaya di Banjarmasin
      </p>
      
      <div className="card-grid">
        {events.map(event => (
          <div key={event.id} className="card">
            <img src={event.gambar} alt={event.nama} className="card-img" />
            <div className="card-content">
              <h3 className="card-title">{event.nama}</h3>
              <p className="card-description">{event.deskripsi}</p>
              <div className="card-meta">
                <span>üìç {event.lokasi}</span>
                <span>üìÖ {new Date(event.tanggal).toLocaleDateString('id-ID', {
                  weekday: 'long',
                  year: 'numeric',
                  month: 'long',
                  day: 'numeric'
                })}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
