import React, { useState, useEffect } from 'react';
import { pemesananAPI } from '../services/api';

const PemesananModal = ({ isOpen, onClose, item, tipe }) => {
  const [formData, setFormData] = useState({
    nama: '',
    email: '',
    no_telepon: '',
    jumlah: 1,
    tanggal: '',
    catatan: ''
  });
  const [loading, setLoading] = useState(false);

  // Format tanggal default (hari ini + 7 hari)
  const getDefaultDate = () => {
    const date = new Date();
    date.setDate(date.getDate() + 7);
    return date.toISOString().split('T')[0];
  };

  useEffect(() => {
    if (isOpen) {
      // Reset form ketika modal dibuka
      setFormData({
        nama: '',
        email: '',
        no_telepon: '',
        jumlah: 1,
        tanggal: getDefaultDate(),
        catatan: ''
      });
    }
  }, [isOpen]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      console.log('🔄 Memulai pemesanan...', { item, tipe, formData });
      
      // Format tanggal untuk backend
      const tanggalISO = new Date(formData.tanggal + 'T00:00:00Z').toISOString();
      
      const pemesananData = {
        nama: formData.nama,
        email: formData.email,
        no_telepon: formData.no_telepon,
        tipe: tipe,
        item_id: item.id,
        jumlah: parseInt(formData.jumlah),
        tanggal: tanggalISO,
        catatan: formData.catatan || ''
      };

      console.log('📤 Data yang dikirim:', pemesananData);

      const response = await pemesananAPI.create(pemesananData);
      console.log('✅ Response sukses:', response.data);
      
      alert('🎉 Pemesanan berhasil dikirim!');
      onClose();
    } catch (error) {
      console.error('❌ Error membuat pemesanan:', error);
      const errorMessage = error.response?.data?.error || error.message;
      alert('😞 Gagal membuat pemesanan: ' + errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Pesan {tipe === 'wisata' ? 'Tiket Wisata' : tipe === 'kuliner' ? 'Makanan' : 'Tiket Event'}</h2>
          <button className="close-btn" onClick={onClose}>×</button>
        </div>
        
        <div className="modal-body">
          <div className="item-info">
            <h3>{item?.nama || 'Item'}</h3>
            <p>{item?.deskripsi || 'Deskripsi item'}</p>
            {tipe === 'wisata' && (
              <p className="price">Harga: {item?.harga_tiket === 0 ? 'Gratis' : `Rp ${item?.harga_tiket?.toLocaleString('id-ID')}`}</p>
            )}
            {tipe === 'kuliner' && (
              <p className="price">Harga: Rp {item?.harga?.toLocaleString('id-ID')}</p>
            )}
          </div>

          <form onSubmit={handleSubmit} className="pemesanan-form">
            <div className="form-group">
              <label>Nama Lengkap *</label>
              <input
                type="text"
                name="nama"
                value={formData.nama}
                onChange={handleChange}
                required
                disabled={loading}
                placeholder="Masukkan nama lengkap"
              />
            </div>

            <div className="form-group">
              <label>Email *</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                disabled={loading}
                placeholder="contoh@email.com"
              />
            </div>

            <div className="form-group">
              <label>No. Telepon *</label>
              <input
                type="tel"
                name="no_telepon"
                value={formData.no_telepon}
                onChange={handleChange}
                required
                disabled={loading}
                placeholder="08123456789"
              />
            </div>

            <div className="form-group">
              <label>Jumlah {tipe === 'kuliner' ? 'Porsi' : 'Tiket'} *</label>
              <input
                type="number"
                name="jumlah"
                value={formData.jumlah}
                onChange={handleChange}
                min="1"
                max="10"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Tanggal {tipe === 'kuliner' ? 'Pengambilan' : 'Kunjungan'} *</label>
              <input
                type="date"
                name="tanggal"
                value={formData.tanggal}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Catatan Tambahan</label>
              <textarea
                name="catatan"
                value={formData.catatan}
                onChange={handleChange}
                rows="3"
                placeholder="Contoh: Untuk wisata - jam kunjungan, untuk kuliner - catatan khusus, dll."
                disabled={loading}
              />
            </div>

            <div className="form-actions">
              <button 
                type="button" 
                onClick={onClose} 
                className="btn-cancel"
                disabled={loading}
              >
                Batal
              </button>
              <button 
                type="submit" 
                className="btn-submit"
                disabled={loading}
              >
                {loading ? '🔄 Memproses...' : '✅ Pesan Sekarang'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PemesananModal;
