import React, { useState, useEffect } from 'react';
import { wisataAPI } from '../services/api';

const Wisata = () => {
  const [wisata, setWisata] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWisata = async () => {
      try {
        const response = await wisataAPI.getAll();
        setWisata(response.data);
      } catch (err) {
        setError('Gagal memuat data wisata');
        console.error('Error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchWisata();
  }, []);

  if (loading) return <div className="loading">Memuat data wisata...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="section">
      <h1 className="section-title">Destinasi Wisata Banjarmasin</h1>
      <p style={{ textAlign: 'center', marginBottom: '2rem', fontSize: '1.2rem' }}>
        Temukan berbagai destinasi wisata menarik di Kota Banjarmasin
      </p>
      
      <div className="card-grid">
        {wisata.map(item => (
          <div key={item.id} className="card">
            <img src={item.gambar} alt={item.nama} className="card-img" />
            <div className="card-content">
              <h3 className="card-title">{item.nama}</h3>
              <p className="card-description">{item.deskripsi}</p>
              <div className="card-meta">
                <span>üìç {item.lokasi}</span>
                <span>‚≠ê {item.rating}</span>
              </div>
              <div className="card-meta" style={{ marginTop: '0.5rem' }}>
                <span>üè∑Ô∏è {item.kategori}</span>
                <span>üí∞ {item.harga_tiket === 0 ? 'Gratis' : `Rp ${item.harga_tiket?.toLocaleString('id-ID')}`}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Wisata;
