import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className="header">
      <nav className="nav-container">
        <Link to="/" className="logo" onClick={closeMenu}>
          {/* Ganti emoji dengan logo custom */}
          <span style={{ marginRight: '10px', fontSize: '24px' }}>üèûÔ∏è</span>
          Pariwisata Banjarmasin
        </Link>
        
        {/* Hamburger Button */}
        <button 
          className={`hamburger ${isMenuOpen ? 'active' : ''}`}
          onClick={toggleMenu}
          aria-label="Toggle menu"
        >
          <span></span>
          <span></span>
          <span></span>
        </button>

        {/* Navigation Links */}
        <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
          <li><Link to="/" onClick={closeMenu}>üè† Beranda</Link></li>
          <li><Link to="/wisata" onClick={closeMenu}>üé´ Wisata</Link></li>
          <li><Link to="/events" onClick={closeMenu}>üìÖ Event</Link></li>
          <li><Link to="/kuliner" onClick={closeMenu}>üçΩÔ∏è Kuliner</Link></li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
